using VEC = vector<int>;
using LS = std::pair<VEC, VEC>;

int asNumber(VEC& digits) {
    int val = 0;
    int power = 1;

    for (auto rit = digits.rbegin(); rit != digits.rend(); ++rit, power *= 10) {
        val += *rit * power;
    }

    return val;
}

VEC asList(int val) {
    VEC list;
    while(val > 0) {
        list.push_back(val % 10);
        val /= 10;
    }
    std::reverse(list.begin(), list.end());
    return list;
}

template <class T>
int numDigits(T number)
{
    int digits = 0;
    if (number < 0) digits = 1; // remove this line if '-' counts as a digit
    while (number) {
        number /= 10;
        digits++;
    }
    return digits;
}


TEST(Warmup, ReturnTrue) {
    if(!returnTrue()) {
        FAIL() << "Should return true!";
    }
}

TEST(Warmup, ReturnPositiveOdd) {
    int ret = returnPositiveOdd();
    if(ret < 0) FAIL() << "returned a negative value (" << ret << "). Expected a positive, odd number";
    if((ret & 1) == 0) FAIL() << "returned an even value (" << ret << "). Expected a positive, odd number";
}

TEST(Warmup, ReturnPrime) {
    int ret = returnPrime();
    if(ret < 0) FAIL() << "returned a negative value (" << ret << "). Expected a prime with two digits";
    if(ret < 10) FAIL() << "returned a single digit value (" << ret << "). Expected a prime with two digits";
    if(ret >= 100) FAIL() << "returned a value with " << numDigits(ret) << " digits (value = " << ret << "). Expected a prime with two digits";
    for(int i=2; (i*i) <= ret; i++){
        if(ret % i == 0 ) FAIL() << "your number ("<< ret <<") is divisible by " << i << ". Expected a prime with two digits";
    }
}


class PlusOne : public testing::TestWithParam<int> {};

TEST_P(PlusOne, CheckNumbers) {
    int number = GetParam();
    VEC digits = asList(number);

    try {
        VEC after = plusOne(digits);
        int output = asNumber(after);
        ASSERT_EQ(output, number + 1);
    } catch (const std::invalid_argument& e) {
        FAIL() << "processNumber() failed due to malformed digit input: " << e.what();
    } catch (const std::exception& e) {
        FAIL() << "processNumber() threw an unexpected std::exception: " << e.what();
    } catch (...) {
        FAIL() << "processNumber() threw an unknown, non-standard exception.";
    }
}

