class Reverse : public testing::TestWithParam<VEC> {};

TEST_P(Reverse, CheckList) {
    VEC input = GetParam();
    VEC expected(input.size());
    std::reverse_copy(std::begin(input), std::end(input), std::begin(expected));
    reverse(input);
    ASSERT_EQ(input, expected);
}

